{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Credit Card",
  "description": "A schema for representing credit card products and their terms.",
  "type": "object",
  "properties": {
    "card_name": {
      "type": "string",
      "description": "The name of the credit card product."
    },
    "issuer_name": {
      "type": "string",
      "description": "The name of the financial institution issuing the card."
    },
    "network": {
      "type": "string",
      "enum": ["Visa", "Mastercard", "American Express", "Discover"],
      "description": "The payment network the card operates on."
    },
    "url": {
      "type": "string",
      "format": "uri",
      "description": "The primary marketing or application URL for the card."
    },
    "pricing_details": {
      "type": "object",
      "properties": {
        "purchase_apr": {
          "type": "number",
          "description": "The Annual Percentage Rate (APR) for purchases."
        },
        "variable_apr_index": {
          "type": "string",
          "description": "The index used for variable APRs (e.g., 'Prime Rate')."
        },
        "variable_apr_margin": {
          "type": "number",
          "description": "The margin added to the index for variable APRs."
        },
        "penalty_apr": {
          "type": "number",
          "description": "The APR applied as a penalty for late payments or other violations."
        },
        "penalty_apr_trigger": {
          "type": "string",
          "description": "The condition that triggers the penalty APR (e.g., 'One late payment')."
        },
        "cash_advance_apr": {
          "type": "number",
          "description": "The APR for cash advances."
        },
        "balance_transfer_apr": {
          "type": "number",
          "description": "The APR for balance transfers."
        },
        "grace_period_days": {
          "type": "integer",
          "description": "The number of days in the grace period for new purchases."
        }
      },
      "required": ["purchase_apr"]
    },
    "fees": {
      "type": "object",
      "properties": {
        "annual_fee": {
          "type": "number",
          "description": "The annual fee for the card."
        },
        "late_fee": {
          "type": "number",
          "description": "The fee for late payments."
        },
        "cash_advance_fee": {
          "type": "string",
          "description": "The fee for cash advances (e.g., '5% of the amount of each advance, but not less than $10')."
        },
        "balance_transfer_fee": {
          "type": "string",
          "description": "The fee for balance transfers (e.g., '3% of the amount of each transfer, but not less than $5')."
        },
        "foreign_transaction_fee": {
          "type": "number",
          "description": "The percentage fee for foreign transactions."
        },
        "returned_payment_fee": {
          "type": "number",
          "description": "The fee for returned payments."
        },
        "over_the_limit_fee": {
          "type": "number",
          "description": "The fee for exceeding the credit limit."
        }
      }
    },
    "rewards_program": {
      "type": "object",
      "properties": {
        "program_name": {
          "type": "string",
          "description": "The name of the rewards program."
        },
        "rewards_type": {
          "type": "string",
          "enum": ["Points", "Cash Back", "Miles", "None"],
          "description": "The type of rewards offered."
        },
        "earn_rate": {
          "type": "string",
          "description": "A description of the rewards earning rate (e.g., '1.5% on all purchases')."
        },
        "expiration_policy": {
          "type": "string",
          "description": "The policy for rewards expiration (e.g., 'Points do not expire')."
        }
      }
    },
    "extraction_tactics": {
      "type": "object",
      "properties": {
        "minimum_payment_calculation": {
          "type": "string",
          "description": "The formula used to calculate the minimum payment (e.g., 'Greater of 1% of the New Balance, or $25')."
        },
        "autopay_default": {
          "type": "string",
          "enum": ["Minimum Payment", "Statement Balance", "Custom Amount"],
          "description": "The default setting for autopay enrollment."
        },
        "unsolicited_credit_limit_increases": {
          "type": "boolean",
          "description": "Whether the issuer provides unsolicited credit limit increases."
        },
        "affiliate_marketing_network": {
          "type": "string",
          "description": "The primary affiliate network used for marketing the card, if known."
        }
      }
    }
  },
  "required": [
    "card_name",
    "issuer_name",
    "network",
    "pricing_details",
    "fees"
  ]
}
